'use strict';

var sizeOf = require( './../lib' );

var s = sizeOf( 'float64' );
console.log( s );
// => 8

s = sizeOf( 'float32' );
console.log( s );
// => 4

s = sizeOf( 'float16' );
console.log( s );
// => 2

s = sizeOf( 'int32' );
console.log( s );
// => 4

s = sizeOf( 'uint32' );
console.log( s );
// => 4

s = sizeOf( 'int16' );
console.log( s );
// => 2

s = sizeOf( 'uint16' );
console.log( s );
// => 2

s = sizeOf( 'int8' );
console.log( s );
// => 1

s = sizeOf( 'uint8' );
console.log( s );
// => 1

s = sizeOf( 'uint8c' );
console.log( s );
// => 1

s = sizeOf( 'complex128' );
console.log( s );
// => 16

s = sizeOf( 'complex64' );
console.log( s );
// => 8
