'use strict';

// MODULES //

var tape = require( 'tape' );
var someAsync = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof someAsync, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a `factory` method', function test( t ) {
	t.strictEqual( typeof someAsync.factory, 'function', 'has method' );
	t.end();
});
