'use strict';

// MODULES //

var bench = require( '@stdlib/bench' );
var isBoolean = require( '@stdlib/assert/is-boolean' ).isPrimitive;
var pkg = require( './../package.json' ).name;
var some = require( './../lib' );


// MAIN //

bench( pkg, function benchmark( b ) {
	var bool;
	var arr;
	var i;

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		arr = [ 0, 0, i+1, i+2, i+3, i+4 ];
		bool = some( arr, 4 );
		if ( !isBoolean( bool ) ) {
			b.fail( 'should return a boolean' );
		}
	}
	b.toc();
	if ( !isBoolean( bool ) ) {
		b.fail( 'should return a boolean' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});

bench( pkg+'::loop', function benchmark( b ) {
	var total;
	var count;
	var bool;
	var arr;
	var i;
	var j;

	total = 4;

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		arr = [ 0, 0, 1, 2, 3, 4 ];
		bool = false;
		count = 0;
		for ( j = 0; j < arr.length; j++ ) {
			if ( arr[ j ] ) {
				count += 1;
				if ( count === total ) {
					bool = true;
					break;
				}
			}
		}
		if ( !isBoolean( bool ) ) {
			b.fail( 'should be a boolean' );
		}
	}
	b.toc();
	if ( !isBoolean( bool ) ) {
		b.fail( 'should be a boolean' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});
