'use strict';

var timeit = require( './../lib' );

var opts;
var code;

opts = {
	'iterations': 1e2,
	'repeats': 5,
	'asynchronous': true
};

code = '';
code += 'var err;';
code += 'var x = Math.pow( Math.random(), 3 );';
code += 'if ( x !== x ) {';
code += 'err = new Error( \'Something went wrong.\' );';
code += 'next( err );';
code += '}';
code += 'setTimeout( next, 0 );';

timeit( code, opts, done );

function done( error, results ) {
    if ( error ) {
        throw error;
    }
    console.dir( results );
}
