'use strict';

var timeit = require( './../lib' );

var opts;
var code;

opts = {
	'iterations': 1e2,
	'repeats': 5,
	'asynchronous': true
};

code = 'var err; var x = Math.pow( Math.random(), 3 ); if ( x !== x ) { err = new Error( \'Something went wrong.\' ); next( err ); } setTimeout( next, 0 );';

timeit( code, opts, done );

function done( error, results ) {
    if ( error ) {
        throw error;
    }
    console.dir( results );
}
