'use strict';

var join = require( 'path' ).join;
var readFileSync = require( '@stdlib/fs/read-file' ).sync;
var timeit = require( './../lib' );

var before;
var code;
var opts;

before = readFileSync( join( __dirname, 'before.txt' ), 'utf8' );
code = readFileSync( join( __dirname, 'code.txt' ), 'utf8' );

opts = {
	'iterations': null,
	'repeats': 3,
	'before': before
};

// Determine a suitable number of iterations
timeit( code, opts, onResults );

function onResults( error, results ) {
	if ( error ) {
		throw error;
	}
	opts.iterations = results.iterations;

	// Time the snippet using the determined number of iterations:
	timeit( code, opts, done );
}

function done( error, results ) {
	if ( error ) {
		throw error;
	}
	console.dir( results );
}
