'use strict';

/**
* Time a snippet.
*
* @module @stdlib/utils/timeit
*
* @example
* var timeit = require( '@stdlib/utils/timeit' );
*
* var code = '';
* code += 'var x = Math.pow( Math.random(), 3 );';
* code += 'if ( x !== x ) {';
* code += 'throw new Error( \'Something went wrong.\' );';
* code += '}';
*
* timeit( code, done );
*
* function done( error, results ) {
*     if ( error ) {
*         throw error;
*     }
*     console.dir( results );
* }
*/

// MODULES //

var timeit = require( './timeit.js' );


// EXPORTS //

module.exports = timeit;
