'use strict';

// MODULES //

var tic = require( '@stdlib/utils/tic' );
var toc = require( '@stdlib/utils/toc' );


// MAIN //

/**
* Returns a sandbox.
*
* @private
* @returns {Object} sandbox
*/
function sandbox() {
	// Note: can restrict global variables here...
	return {
		'__tic__': tic,
		'__toc__': toc,
		'setTimeout': setTimeout,
		'clearTimeout': clearTimeout
	};
} // end FUNCTION sandbox()


// EXPORTS //

module.exports = sandbox;
