'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var tmpdir = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof tmpdir, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a string', function test( t ) {
	t.equal( typeof tmpdir(), 'string', 'returns a string' );
	t.end();
});

tape( 'the function checks the `TEMP` environment variable on Windows platforms', function test( t ) {
	var tmpdir;
	var dir;
	var env;

	tmpdir = proxyquire( './../lib/tmpdir.js', {
		'@stdlib/utils/is-windows': true
	});

	env = process.env;
	process.env = {
		'TEMP': 'C:\\foo\\bar\\baz'
	};

	dir = tmpdir();
	t.strictEqual( dir, process.env.TEMP, 'returns directory' );

	process.env = env;

	t.end();
});

