tape( 'the function removes trailing slashes on Windows paths (non-device)', function test( t ) {
	var tmpdir;
	var dir;
	var env;

	tmpdir = proxyquire( './../lib/tmpdir.js', {
		'@stdlib/utils/is-windows': true
	});

	env = process.env;
	process.env = {
		'TEMP': 'C:\\foo\\bar\\baz\\'
	};

	dir = tmpdir();
	t.strictEqual( dir, 'C:\\foo\\bar\\baz', 'returns directory' );

	process.env = {
		'TEMP': 'C:\\'
	};

	dir = tmpdir();
	t.strictEqual( dir, 'C:\\', 'returns directory' );

	process.env = env;

	t.end();
});

