'use strict';

// MODULES //

var leftTrim = require( '@stdlib/utils/left-trim-string' );
var rightTrim = require( '@stdlib/utils/right-trim-string' );


// TRIM //

/**
* Trim whitespace characters from beginning and end of a string.
*
* @param {string} str - input string
* @throws {TypeError} str must be a string primitive
* @returns {string} output string
*
* @example
* var out = trim( '   Whitespace   ' );
* // returns 'Whitespace'
* @example
* var out = trim( '\t\t\tTabs\t\t\t' );
* // returns 'Tabs'
* @example
* var out = trim( '\n\n\nNew Lines\n\n\n' ) );
* // returns 'New Lines'
*/
function trim( str ) {
	return leftTrim( rightTrim( str ) );
} // end FUNCTION trim()


// EXPORTS //

module.exports = trim;
