'use strict';

// MODULES //

var tape = require( 'tape' );
var noop = require( '@stdlib/utils/noop' );
var wrap = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof noop, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function will throw an error if not provided a function', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5,
		null,
		NaN,
		undefined,
		[],
		{}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws type error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			wrap( value );
		};
	}
});

tape( 'the function returns a function', function test( t ) {
	t.equal( typeof wrap( noop ), 'function', 'returns a function' );
	t.end();
});

tape( 'the function returns a function having the same signature', function test( t ) {
	function add( a, b, c ) {
		return a + b + c;
	}

	var f = wrap( add );
	t.equal( add( 1, 2, 3 ), f( 1, 2, 3 ), 'returns same results' );
	t.end();
});

tape( 'the function returns any trapped errors', function test( t ) {
	function fcn() {
		throw new Error( 'beep boop' );
	}
	var f = wrap( fcn );
	var out = f();

	t.equal( out instanceof Error, true, 'returns an error instance' );
	t.equal( out.message, 'beep boop', 'returns trapped error' );
	t.end();
});

tape( 'the function supports setting the `this` context', function test( t ) {
	function fcn() {
		/* jshint validthis: true */
		return this.beep;
	}
	var f = wrap( fcn, { 'beep': 'boop' } );
	t.equal( f(), 'boop', 'returns property bound to `this` context' );
	t.end();
});

tape( 'the function will not trap and return errors which occur in nested asynchronous function scopes', function test( t ) {
	function fcn() {
		setTimeout( clbk, 0 );
	}
	function clbk() {
		t.throws( createError, Error, 'throws an untrapped error' );
		t.end();
	}
	function createError() {
		throw new Error( 'beep boop' );
	}
	var f = wrap( fcn );
	t.equal( f(), void 0, 'returns undefined' );
});
