'use strict';

/**
* If a function does not return an error, invoke a callback with the function result; otherwise, invoke a second function `y`.
*
* @module @stdlib/utils/try-then-async
*
* @example
* var randu = require( '@stdlib/math/base/random/randu' );
* var trythenAsync = require( '@stdlib/utils/try-then-async' );
*
* function x( clbk ) {
*     setTimeout( onTimeout, 0 );
*     function onTimeout() {
*         if ( randu() > 0.5 ) {
*             return clbk( null, 1.0 );
*         }
*         clbk( new Error( 'beep' ) );
*     }
* }
*
* function y( clbk ) {
*     setTimeout( onTimeout, 0 );
*     function onTimeout() {
*         clbk( null, -1.0 );
*     }
* }
*
* function done( error, result ) {
*     if ( error ) {
*         console.log( error.message );
*     }
*     console.log( result );
* }
*
* trythenAsync( x, y, done );
*/

// MODULES //

var trythenAsync = require( './try_then_async.js' );


// EXPORTS //

module.exports = trythenAsync;
