'use strict';

var typemax = require( './../lib' );

var m = typemax( 'float64' );
console.log( m );
// => +infinity

m = typemax( 'float32' );
console.log( m );
// => +infinity

m = typemax( 'float16' );
console.log( m );
// => +infinity

m = typemax( 'int32' );
console.log( m );
// => 2147483647

m = typemax( 'uint32' );
console.log( m );
// => 4294967295

m = typemax( 'int16' );
console.log( m );
// => 32767

m = typemax( 'uint16' );
console.log( m );
// => 65535

m = typemax( 'int8' );
console.log( m );
// => 127

m = typemax( 'uint8' );
console.log( m );
// => 255

m = typemax( 'uint8c' );
console.log( m );
// => 255
