'use strict';

/**
* FUNCTION: typeOf( v )
*	Wraps the `typeof` operator.
*
* @param {*} v - input value
* @returns {String} string indicating the type of an unevaluated operand
*/
function typeOf( v ) {
	// http://wiki.ecmascript.org/doku.php?id=harmony:typeof_null
	return ( v === null ) ? 'null' : typeof v;
} // end FUNCTION typeOf()


// EXPORTS //

module.exports = typeOf;
