'use strict';

/**
* Invoke a function until a test condition is true.
*
* @module @stdlib/utils/until-async
*
* @example
* var untilAsync = require( '@stdlib/utils/until-async' );
*
* function predicate( i, clbk ) {
*     clbk( null, i >= 5 );
* }
*
* function fcn( i, next ) {
*     setTimeout( onTimeout, i );
*     function onTimeout() {
*         console.log( 'beep: %d', i );
*         next();
*     }
* }
*
* function done( error ) {
*     if ( error ) {
*         throw error;
*     }
* }
*
* untilAsync( predicate, fcn, done );
*/

// MODULES //

var untilAsync = require( './until_async.js' );


// EXPORTS //

module.exports = untilAsync;
