'use strict';

var unzip = require( './../lib' );

var unzipped;
var elapsed;
var start;
var diff;
var arr;
var i;

// Simulate some data...
arr = new Array( 1000 );

for ( i = 0; i < arr.length; i++ ) {
	arr[ i ] = [
		Date.now(),
		Math.random() * 100
	];
}

start = process.hrtime();
for ( var j = 0; j < 1e6; j++ ) {
	unzipped = unzip( arr );
}
diff = process.hrtime( start );

// Elapsed time:
elapsed = diff[ 0 ] + diff[ 1 ]*1e-9;

// Results:
console.log( 'Ops/sec: %d ops', 1e6/elapsed );
console.log( 'Time per op: %d ms', elapsed/1e6*1000 );
