'use strict';

/**
* Unzip a zipped array (i.e., a nested array of tuples).
*
* @module @stdlib/math/generics/utils/unzip
*
* @example
* var unzip = require( '@stdlib/math/generics/utils/unzip' );
*
* var arr = [ [1,'a',3], [2,'b',4] ];
* var out = unzip( arr );
* // returns [ [1,2], ['a','b'], [3,4] ];
*
* arr = [ [1,'a',3], [2,'b',4] ];
* out = unzip( arr, [0,2] );
* // returns [ [1,2], [3,4] ];
*
*/

// MODULES //

var unzip = require( './unzip.js' );


// EXPORTS //

module.exports = unzip;
