'use strict';

var uppercase = require( './../lib' );

var str;

str = uppercase( 'Beep' );
console.log( str );
// => 'BEEP'

str = uppercase( 'BeEp' );
console.log( str );
// => 'BEEP'

str = uppercase( 'Beep BOOP' );
console.log( str );
// => 'BEEP BOOP'

str = uppercase( '$**_beep_BoOp_**$' );
console.log( str );
// => '$**_BEEP_BOOP_**$'

str = uppercase( '' );
console.log( str );
// => ''
