'use strict';

var objectValuesIn = require( './../lib' );

function Foo() {
	this.beep = 'boop';
	this.a = {
		'b': 'c'
	};
	return this;
}

Foo.prototype.foo = [ 'bar' ];

var obj = new Foo();
var values = objectValuesIn( obj );

console.log( values );
// e.g., => [ 'boop', {'b':'c'}, [ 'bar' ] ]

