'use strict';

/**
* Invoke a function while a test condition is true.
*
* @module @stdlib/utils/while-async
*
* @example
* var whileAsync = require( '@stdlib/utils/while-async' );
*
* function predicate( i, clbk ) {
*     clbk( null, i < 5 );
* }
*
* function fcn( i, next ) {
*     setTimeout( onTimeout, i );
*     function onTimeout() {
*         console.log( 'beep: %d', i );
*         next();
*     }
* }
*
* function done( error ) {
*     if ( error ) {
*         throw error;
*     }
* }
*
* whileAsync( predicate, fcn, done );
*/

// MODULES //

var whileAsync = require( './while_async.js' );


// EXPORTS //

module.exports = whileAsync;
