'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var zip = require( './../lib' );

var zipped;
var len;
var y1;
var y2;
var y3;
var x;
var i;

// Simulate some data...
x = new Array( 100 );
len = x.length;
y1 = new Array( len );
y2 = new Array( len );
y3 = new Array( len );

for ( i = 0; i < len; i++ ) {
	x[ i ] = Date.now();
	y1[ i ] = randu() * 100;
	y2[ i ] = randu() * 100;
	y3[ i ] = randu();
}

zipped = zip( x, y1, y2, y3 );

console.log( zipped.join( '\n' ) );
