'use strict';

/**
* Generate array tuples from input arrays.
*
* @module @stdlib/math/generics/utils/zip
*
* @example
* var zip = require( '@stdlib/math/generics/utils/zip' );
*
* var zipped = zip( [1,2], ['a','b'] );
* // returns [ [1,'a'], [2,'b'] ]
*
* var opts = {
*    'trunc': false
* };
*
* var zipped = zip( [1,2,3], ['a','b'], opts );
* // returns [ [1,'a'], [2,'b'], [3,null] ]
*/

// MODULES //

var zip = require( './zip.js' );


// EXPORTS //

module.exports = zip;
